                if (id === 'body' || id === 'body_head' || id === 'ears' || id === 'nose' || id === 'age' || id.slice(0,4) === 'mouth') {
                    affectedList = skinLayers;
                    var myKey = 'skinColor';
                    //colorSkin(_color);
                    classPrefix = "skin";
                }
